const Client = require('node-radius-client'); // eslint-disable-line
const {
  dictionaries: {
    rfc2865: {
      file,
      attributes,
    },
  },
  attributesToArray,
} = require('node-radius-utils');

const client = new Client({
  host: '10.0.0.123',
  dictionaries: [
    file,
  ],
});

client.accessRequest({
  secret: 'super-secret',
  attributes: {
    [attributes.USER_NAME]: 'bobross',
    [attributes.USER_PASSWORD]: 'hunter2',
  },
}).then((result) => {
  console.log('result', result);
}).catch((error) => {
  console.log('error', error);
});
